operations.body:
query GetAllUsers($name: String) {
  users(name: $name) {
    id
    name
    email
    age
  }
}

query GetUser($id: ID!) {
  user(id: $id) {
    id
    name
    email
    age
  }
}

query GetAllProducts($name: String) {
  products(name: $name) {
    id
    name
    price
    description
    inStock
  }
}

query GetProduct($id: ID!) {
  product(id: $id) {
    id
    name
    price
    description
    inStock
  }
}

query GetCombinedData($userName: String, $productName: String) {
  users(name: $userName) {
    id
    name
    email
  }
  products(name: $productName) {
    id
    name
    price
    inStock
  }
}
